(()=>{"use strict";const e=[{description:"Attend morning session",completed:!1,index:0},{description:"lanch breack",completed:!1,index:1},{description:"afternoon session",completed:!0,index:2},{description:"evening session",completed:!1,index:3}];class t{static getDataAll(){return e}static getData(e){return t.getDataAll().filter((t=>t.index===e))[0]}static displayTask(e){return t.getDataAll().forEach((t=>{const n=document.createElement("li");n.className="list-group-item d-flex justify-content-between ",n.innerHTML=`<div for="check">${t.description}</div>\n  <input class="form-check-input ml-auto" type="checkbox" value="" id="check">\n      `,e.appendChild(n)})),e}}const n=document.querySelector(".list");t.displayTask(n)})();